import { useState, useEffect } from 'react'
import styled from 'styled-components'

import { SearchBox } from '../components/SearchBox'
import { PropertyCard } from '../components/PropertyCard'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  /* background-color: #0070ff; */
  color: #fff;
  align-items: center;
`

const Title = styled.h1`
  font-size: 48px;
  line-height: 1.2;
  font-weight: 500;
  margin-bottom: 0.75rem;
`

const SearchContainer = styled.div`
  /* height: 100vh; */
  width: 30%;
`

export const Search = () => {
  const [propertyData, setPropertyData] = useState(null)
  const [search, setSearch] = useState('')
  const [id, setId] = useState(null)
  const [loading, setLoading] = useState(true)

  useEffect(() => {
    async function fetchData () {
      // demo request to API (ensure it is running!)
      const resp = await fetch(`/lrProperty/${id}`)
      const json = await resp.json()

      if (json.success) {
        setPropertyData(json.data)
        setLoading(false)
      }
    }

    if (id) fetchData()
  }, [id])

  function handleSubmit (event) {
    event.preventDefault()
    setId(search)
  }

  console.log(search)

  return (
    <Container>
      <Title>Find the best property for you</Title>
      <SearchContainer>
        <SearchBox
          placeholder='Enter your ID'
          handleChange={event => setSearch(event.target.value)}
          handleSubmit={handleSubmit}
        />
      </SearchContainer>
      {loading ? null : <PropertyCard item={propertyData} />}
    </Container>
  )
}
