import { useState } from 'react'
import styled from 'styled-components'

import { SearchBox } from '../components/SearchBox'
import { PropertyCard } from '../components/PropertyCard'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  /* background-color: #0070ff; */
  align-items: center;
`

const Title = styled.h1`
  color: #fff;
  font-size: 48px;
  line-height: 1.2;
  font-weight: 500;
  margin-bottom: 2rem;
`

const SearchContainer = styled.form`
  /* height: 100vh; */
  width: 30%;
`

const SearchOptionsContainer = styled.div`
  display: flex;
  flex-direction: row;
  color: #fff;
  margin: 1rem 0;
  justify-content: center;
  align-items: center;

  p {
    margin: 0;
    font-size: 20px;
  }
`

const SearchLabel = styled.label`
  font-size: 15px;
`

const SearchOption = styled.input`
  margin: 0 10px;
  width: 17px;
  height: 17px;
  cursor: pointer;
`

export const Search = () => {
  const [propertyData, setPropertyData] = useState(null)
  const [search, setSearch] = useState('')
  // const [id, setId] = useState('')
  // const [postcode, setPostcode] = useState('')
  // const [street, setStreet] = useState('')
  const [option, setOption] = useState('')
  // const [data, setData] = useState([])
  // const [loading, setLoading] = useState(true)

  // useEffect(() => {
  //   async function fetchData () {
  //     // demo request to API (ensure it is running!)
  //     const resp = await fetch('/lrProperty')
  //     const json = await resp.json()

  //     if (json.success) {
  //       setData(json.data)
  //     }
  //   }

  //   fetchData()
  // }, [])

  async function handleSubmit (event) {
    event.preventDefault()

    let url

    if (option === 'id') {
      url = `/lrProperty/id/${search}`
    } 
    else if (option === 'postcode') {
      const outcode = search.startsWith('E1' || 'E2' || 'E10' || 'E11').toUpperCase()
      const incode = ''
      url = `/lrProperty/postcode/:outcode/:incode`
    }

    const resp = await fetch(url)
    const json = await resp.json()

    if (json.success) {
      setPropertyData(json.lrProperty)
      setSearch('')
    }
  }

  function handleChange (event) {
    setSearch(event.target.value)
  }

  return (
    <Container>
      <Title>Find the best property for you</Title>
      <SearchContainer onSubmit={handleSubmit}>
        <SearchOptionsContainer>
          <p>Search by:</p>
          <SearchLabel>
            <SearchOption
              type='radio'
              name='option'
              value='id'
              onChange={event => setOption(event.target.value)}
            />
            ID
          </SearchLabel>
          <SearchLabel>
            <SearchOption
              type='radio'
              name='option'
              value='postcode'
              onChange={event => setOption(event.target.value)}
            />
            Postcode
          </SearchLabel>
          <SearchLabel>
            <SearchOption
              type='radio'
              name='option'
              value='street'
              onChange={event => setOption(event.target.value)}
            />
            Street
          </SearchLabel>
        </SearchOptionsContainer>
        <SearchBox
          name='search'
          value={search}
          placeholder='Search by ID, Postcode, Street'
          handleChange={handleChange}
        />
      </SearchContainer>
      {propertyData ? <PropertyCard item={propertyData} /> : null}
    </Container>
  )
}
