import { createGlobalStyle } from 'styled-components'

import GothamRoundedBold from './assets/fonts/gothamrounded-bold.otf'
import GothamRoundedLight from './assets/fonts/gothamrounded-light.otf'
import GothamRoundedMedium from './assets/fonts/gothamrounded-medium.otf'

export const GlobalStyle = createGlobalStyle`

@font-face {
  font-family: "Gotham Rounded Bold";
  font-style: normal;
  src: url(${GothamRoundedBold}) format('otf');
}

@font-face {
  font-family: "Gotham Rounded Light";
  font-style: normal;
  src: url(${GothamRoundedLight}) format('otf');
}

@font-face {
  font-family: "Gotham Rounded Med";
  font-style: normal;
  src: url(${GothamRoundedMedium}) format('otf');
}

html {
  line-height: 1.5px;
  box-sizing: border-box;
  height: 100%;
}

*,
*:before,
*:after {
  box-sizing: inherit;
}

body {
  margin: 0;
}

