import { useState, useEffect } from 'react'
import styled from 'styled-components'

import { SearchBox } from '../components/SearchBox'
import { PropertyCard } from '../components/PropertyCard'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  /* background-color: #0070ff; */
  color: #fff;
  align-items: center;
`

const Title = styled.h1`
  font-size: 48px;
  line-height: 1.2;
  font-weight: 500;
  margin-bottom: 0.75rem;
`

const SearchContainer = styled.div`
  /* height: 100vh; */
  width: 30%;
`

export const Search = () => {

  const [propertyData, setPropertyData] = useState(null)
  // const [id, setId] = useState('')
  const [search, setSearch] = useState('')

  function handleSubmit(event) {
    event.preventDefault()

    useEffect(() => {
      async function fetchData () {
        // demo request to API (ensure it is running!)
        const resp = await fetch(`/lrProperty/${search}`)
        const json = await resp.json()

        if (json.success) {
          setPropertyData(json.data)
          setLoading(false)
        }
      }

      fetchData()
    }, [])
  }

  return (
    <Container>
      <Title>Find the best property for you</Title>
      <SearchContainer>
        <SearchBox
          placeholder='Enter your ID'
          handleChange={event => setSearch(event.target.value)}
          handleSubmit={handleSubmit}
        />
      </SearchContainer>
      {propertyData ? <PropertyCard item={propertyData} /> : null}
    </Container>
  )
}
