import { useState } from 'react'
import styled from 'styled-components'

import { SearchBox } from '../components/SearchBox'
import { PropertyCard } from '../components/PropertyCard'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  /* background-color: #0070ff; */
  align-items: center;
`

const Title = styled.h1`
  color: #fff;
  font-size: 48px;
  line-height: 1.2;
  font-weight: 500;
  margin-bottom: 0.75rem;
`

const SearchContainer = styled.div`
  /* height: 100vh; */
  width: 30%;
`

export const Search = () => {
  const [propertyData, setPropertyData] = useState(null)
  const [id, setId] = useState('')
  const [data, setData] = useState({})
  // const [loading, setLoading] = useState(true)

  useEffect(() => {
    async function fetchData () {
      // demo request to API (ensure it is running!)
      const resp = await fetch('/lrProperty')
      const json = await resp.json()

      console

      if (json.success) {
        setPropertyData(json.data)
        setLoading(false)
      }
    }

    if (id) fetchData()
  }, [id])

  async function handleSubmit (event) {
    event.preventDefault()

    const resp = await fetch(`/lrProperty/id/${id}`)
    const json = await resp.json()

    if (json.success) {
      setPropertyData(json.data)
      setId('')
    }
  }

  function handleChange (event) {
    setId(event.target.value)
  }

  return (
    <Container>
      <Title>Find the best property for you</Title>
      <SearchContainer>
        <SearchBox
          name='id'
          value={id}
          placeholder='Enter your ID'
          handleChange={handleChange}
          handleSubmit={handleSubmit}
        />
      </SearchContainer>
      {propertyData ? <PropertyCard item={propertyData} /> : null}
    </Container>
  )
}
