import { useState } from 'react'
import styled from 'styled-components'

import { SearchBox } from '../components/SearchBox'
import { PropertyCard } from '../components/PropertyCard'

const Container = styled.div`
  display: flex;
  flex-direction: column;
  /* background-color: #0070ff; */
  align-items: center;
`

const Title = styled.h1`
  color: #fff;
  font-size: 48px;
  line-height: 1.2;
  font-weight: 500;
  margin-bottom: 0.75rem;
`

const SearchContainer = styled.div`
  /* height: 100vh; */
  width: 30%;
`

const SearchOptionsContainer = styled.div`
  display: flex;
  flex-direction: row;
  color: #fff;
  margin: 1rem 0;
  justify-content: center;
  align-items: center;

  p {
    margin: 0;
  }
`

const SearchLabel = styled.label`
  font-size: 15px;
`

const SearchOption = styled.input`
  margin: 0 10px;
`

export const Search = () => {
  const [propertyData, setPropertyData] = useState(null)
  const [search, setSearch] = useState('')
  const [id, setId] = useState('')
  const [postcode, setPostcode] = useState('')
  const [street, setStreet] = useState('')
  // const [data, setData] = useState([])
  // const [loading, setLoading] = useState(true)

  // useEffect(() => {
  //   async function fetchData () {
  //     // demo request to API (ensure it is running!)
  //     const resp = await fetch('/lrProperty')
  //     const json = await resp.json()

  //     if (json.success) {
  //       setData(json.data)
  //     }
  //   }

  //   fetchData()
  // }, [])

  async function handleSubmit (event) {
    event.preventDefault()

    const resp = await fetch(`/lrProperty/${search}`)
    const json = await resp.json()

    if (json.success) {
      setPropertyData(json.data)
      setSearch('')
    }
  }

  function handleChange (event) {
    setSearch(event.target.value)
  }

  return (
    <Container>
      <Title>Find the best property for you</Title>
      <SearchContainer>
        <SearchOptionsContainer>
          <p>Search by:</p>
          <SearchOption type='radio' name='id' value={id} />
          <SearchLabel for='id'>ID</SearchLabel>
          <SearchOption type='radio' name='postcode' value={postcode} />
          <SearchLabel for='postcode'>Postcode</SearchLabel>
          <SearchOption type='radio' name='street' value={street} />
          <SearchLabel for='street'>Street</SearchLabel>
        </SearchOptionsContainer>
        <SearchBox
          name='search'
          value={search}
          placeholder='Enter your ID'
          handleChange={handleChange}
          handleSubmit={handleSubmit}
        />
      </SearchContainer>
      {propertyData ? <PropertyCard item={propertyData} /> : null}
    </Container>
  )
}
